--
-- 表的结构 `ai_history`
--

CREATE TABLE `ai_history` (
  `id` int(11) NOT NULL,
  `year` int(4) NOT NULL,
  `event_description` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

--
-- 转存表中的数据 `ai_history`
--

INSERT INTO `ai_history` (`id`, `year`, `event_description`) VALUES
(1, 1950, '艾伦·图灵发表《计算机器与智能》，图灵测试打开了通向人工智能的大门。'),
(2, 1951, 'Marvin Minsky 和 Dean Edmonds 使用3000个真空管模拟由40个神经元组成的网络，开发了第一个人工神经网络SNARC。'),
(3, 1952, 'Arthur Samuel开发了世界上第一个自学游戏程序Samuel Checkers-Playing Program。'),
(4, 1956, '约翰·麦卡锡、马文·明斯基等在研讨会中创造了“人工智能”一词。'),
(5, 1958, '弗兰克·罗森布拉特开发了感知器，这是现代神经网络的基础。'),
(6, 1959, '亚瑟·塞缪尔提出“机器学习”一词，介绍计算机超越程序员的可能性。'),
(7, 1964, 'Daniel Bobro开发了STUDENT，早期自然语言处理程序。'),
(8, 1965, 'Edward Feigenbaum等开发了专家系统Dendral，帮助化学家识别未知有机分子。'),
(9, 1966, 'Joseph Weizenbaum创建了Eliza，能与人类对话并模拟情感。'),
(10, 1968, 'Terry Winograd开发了SHRDLU，早期的多模态人工智能系统。'),
(11, 1969, 'Arthur Bryson等描述了反向传播算法，深度学习的基础。'),
(12, 1973, '《人工智能：综合调查》报告发布，导致英国减少对AI的资助。'),
(13, 1980, 'Lisp机器商业化，标志着人工智能复兴，但市场崩溃后研究发展受限。'),
(14, 1981, 'Danny Hillis设计了并行计算机，为现代GPU架构提供了基础。'),
(15, 1984, '马文·明斯基等提出“人工智能冬天”，警告人工智能炒作可能导致行业崩溃。'),
(16, 1985, 'Judea Pearl引入贝叶斯网络，提供计算机处理不确定性的统计技术。'),
(17, 1988, '彼得·布朗等提出语言翻译的统计方法，为机器翻译的研究奠定基础。'),
(18, 1989, 'Yann LeCun等使用卷积神经网络（CNN）识别手写字符，推动了神经网络的应用。'),
(19, 1997, 'IBM的“深蓝”击败国际象棋世界冠军加里·卡斯帕罗夫。'),
(20, 2000, '神经概率语言模型提出，使用前馈神经网络建模语言。'),
(21, 2006, '李飞飞开始研究ImageNet，成为图像识别算法竞赛的基础。'),
(22, 2009, 'Rajat Raina等提出使用GPU进行大规模深度无监督学习的想法。'),
(23, 2011, 'Jürgen Schmidhuber等开发第一个CNN，赢得交通标志识别竞赛；苹果发布Siri语音助理。'),
(24, 2012, 'Geoffrey Hinton等开发深度CNN架构，赢得ImageNet挑战。'),
(25, 2013, '天河二号成为世界最快超级计算机，DeepMind引入深度强化学习，谷歌推出Word2vec。'),
(26, 2014, 'Ian Goodfellow等创造生成对抗网络（GAN）；Facebook推出DeepFace。'),
(27, 2016, 'DeepMind的AlphaGo击败围棋顶尖选手李世石，优步启动自动驾驶汽车试点。'),
(28, 2017, '斯坦福研究人员发表扩散模型论文，谷歌提出Transformer概念，史蒂芬·霍金警告AI潜在风险。'),
(29, 2018, 'Cimon太空机器人开发，OpenAI发布GPT，Groove X推出Lovot机器人。'),
(30, 2019, '微软推出Turing NLG，谷歌的深度学习算法在肺癌检测中超越放射科医生。'),
(31, 2020, 'OpenAI发布GPT-3，英伟达推出Omniverse平台。'),
(32, 2021, 'OpenAI推出Dall-E多模态AI系统，生成图像。'),
(33, 2022, '谷歌工程师Blake Lemoine泄密并声称LaMDA架构具有感知能力，DeepMind推出AlphaTensor。'),
(34, 2023, 'OpenAI推出GPT-4多模态LLM，埃隆·马斯克等警告暂停更强大的AI训练。');

